import React, { useState, useEffect } from 'react';
import { Container, Paper, TextField, Button, Typography, Box, Slider, Grid } from '@mui/material';
import { MapContainer, TileLayer, Marker, Polyline, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { sampleRouteData } from './data/sampleData';

// Fix for default marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Custom bus icon
const busIcon = new L.Icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32]
});

// Bus simulation component
function BusSimulation({ route, isSimulating, progress, onPositionUpdate }) {
  const [position, setPosition] = useState(null);
  const [currentStop, setCurrentStop] = useState(0);
  const map = useMap();

  useEffect(() => {
    if (!route || route.length < 2 || !isSimulating) return;

    // Calculate current position based on progress
    const totalSegments = route.length - 1;
    const segmentProgress = progress * totalSegments;
    const currentSegment = Math.floor(segmentProgress);
    const segmentFraction = segmentProgress - currentSegment;

    if (currentSegment < totalSegments) {
      const start = route[currentSegment];
      const end = route[currentSegment + 1];

      const newLat = start.stop_lat + (end.stop_lat - start.stop_lat) * segmentFraction;
      const newLng = start.stop_lon + (end.stop_lon - start.stop_lon) * segmentFraction;

      setPosition([newLat, newLng]);
      setCurrentStop(currentSegment);

      // Calculate speed and remaining distance
      const totalDistance = calculateDistance(start, end);
      const remainingDistance = totalDistance * (1 - segmentFraction);
      const speed = totalDistance * 0.001 * 3600; // km/h

      onPositionUpdate({
        currentStop: currentSegment,
        nextStop: currentSegment + 1,
        speed: speed.toFixed(1),
        remainingDistance: remainingDistance.toFixed(1),
        progress: (progress * 100).toFixed(1)
      });

      map.setView([newLat, newLng], map.getZoom());
    }
  }, [route, progress, isSimulating, map, onPositionUpdate]);

  return position ? (
    <Marker position={position} icon={busIcon}>
    </Marker>
  ) : null;
}

// Calculate distance between two points in kilometers
function calculateDistance(point1, point2) {
  const R = 6371; // Earth's radius in km
  const dLat = (point2.stop_lat - point1.stop_lat) * Math.PI / 180;
  const dLon = (point2.stop_lon - point1.stop_lon) * Math.PI / 180;
  const a = 
    Math.sin(dLat/2) * Math.sin(dLat/2) +
    Math.cos(point1.stop_lat * Math.PI / 180) * Math.cos(point2.stop_lat * Math.PI / 180) * 
    Math.sin(dLon/2) * Math.sin(dLon/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
}

function App() {
  const [locations, setLocations] = useState([
    { stop_lat: '', stop_lon: '', stop_name: '' }
  ]);
  const [optimizedRoute, setOptimizedRoute] = useState(null);
  const [error, setError] = useState('');
  const [isSimulating, setIsSimulating] = useState(false);
  const [progress, setProgress] = useState(0);
  const [simulationSpeed, setSimulationSpeed] = useState(1);
  const [busInfo, setBusInfo] = useState({
    currentStop: 0,
    nextStop: 1,
    speed: '0.0',
    remainingDistance: '0.0',
    progress: '0.0'
  });

  useEffect(() => {
    let interval;
    if (isSimulating && optimizedRoute) {
      interval = setInterval(() => {
        setProgress((prev) => {
          const next = prev + (0.001 * simulationSpeed);
          if (next >= 1) {
            setIsSimulating(false);
            return 0;
          }
          return next;
        });
      }, 50);
    }
    return () => clearInterval(interval);
  }, [isSimulating, simulationSpeed, optimizedRoute]);

  const handleAddLocation = () => {
    setLocations([...locations, { stop_lat: '', stop_lon: '', stop_name: '' }]);
  };

  const handleLocationChange = (index, field, value) => {
    const newLocations = [...locations];
    newLocations[index][field] = value;
    setLocations(newLocations);
  };

  const handleLoadSampleData = () => {
    const formattedData = sampleRouteData.map(location => ({
      stop_lat: location.stop_lat.toString(),
      stop_lon: location.stop_lon.toString(),
      stop_name: location.stop_name
    }));
    setLocations(formattedData);
  };

  const handleOptimize = async () => {
    try {
      setError('');
      console.log('Sending optimization request...');

      const locationsData = locations.map(loc => ({
        ...loc,
        stop_lat: parseFloat(loc.stop_lat),
        stop_lon: parseFloat(loc.stop_lon)
      }));

      console.log('Request payload:', { locations: locationsData });

      const response = await fetch('/api/optimize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ locations: locationsData }),
      });

      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Response data:', data);

      if (response.ok) {
        setOptimizedRoute(data.route);
        setError('');
      } else {
        setError(data.message || 'Failed to optimize route');
      }
    } catch (err) {
      console.error('Error during optimization:', err);
      setError(`Failed to connect to server: ${err.message}`);
    }
  };

  const handleStartSimulation = () => {
    setProgress(0);
    setIsSimulating(true);
  };

  const handleStopSimulation = () => {
    setIsSimulating(false);
    setProgress(0);
  };

  const center = locations[0]?.stop_lat && locations[0]?.stop_lon
    ? [parseFloat(locations[0].stop_lat), parseFloat(locations[0].stop_lon)]
    : [12.9716, 77.5946];

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Route Optimization
        </Typography>

        <Paper sx={{ p: 2, mb: 2 }}>
          <Box sx={{ mb: 2 }}>
            <Button 
              variant="outlined" 
              onClick={handleLoadSampleData} 
              sx={{ mr: 1 }}
            >
              Load Sample Data
            </Button>
            <Button 
              variant="outlined" 
              onClick={() => setLocations([{ stop_lat: '', stop_lon: '', stop_name: '' }])} 
              sx={{ mr: 1 }}
            >
              Clear All
            </Button>
          </Box>

          {locations.map((location, index) => (
            <Box key={index} sx={{ mb: 2 }}>
              <Typography variant="h6">Location {index + 1}</Typography>
              <TextField
                label="Name"
                value={location.stop_name}
                onChange={(e) => handleLocationChange(index, 'stop_name', e.target.value)}
                sx={{ mr: 1, mb: 1 }}
              />
              <TextField
                label="Latitude"
                type="number"
                value={location.stop_lat}
                onChange={(e) => handleLocationChange(index, 'stop_lat', e.target.value)}
                sx={{ mr: 1, mb: 1 }}
              />
              <TextField
                label="Longitude"
                type="number"
                value={location.stop_lon}
                onChange={(e) => handleLocationChange(index, 'stop_lon', e.target.value)}
                sx={{ mb: 1 }}
              />
            </Box>
          ))}

          <Button variant="outlined" onClick={handleAddLocation} sx={{ mr: 1 }}>
            Add Location
          </Button>
          <Button 
            variant="contained" 
            onClick={handleOptimize}
            disabled={locations.some(loc => !loc.stop_lat || !loc.stop_lon || !loc.stop_name)}
            sx={{ mr: 1 }}
          >
            Optimize Route
          </Button>

          {optimizedRoute && (
            <>
              <Button
                variant="contained"
                color="success"
                onClick={handleStartSimulation}
                disabled={isSimulating}
                sx={{ mr: 1 }}
              >
                Start Simulation
              </Button>
              <Button
                variant="contained"
                color="error"
                onClick={handleStopSimulation}
                disabled={!isSimulating}
              >
                Stop Simulation
              </Button>
              <Box sx={{ mt: 2 }}>
                <Typography gutterBottom>Simulation Speed</Typography>
                <Slider
                  value={simulationSpeed}
                  onChange={(e, newValue) => setSimulationSpeed(newValue)}
                  min={1}
                  max={10}
                  step={1}
                  marks
                  valueLabelDisplay="auto"
                  sx={{ width: 200 }}
                />
              </Box>
            </>
          )}

          {error && (
            <Typography color="error" sx={{ mt: 2 }}>
              {error}
            </Typography>
          )}
        </Paper>

        <Paper sx={{ height: '500px', width: '100%', overflow: 'hidden', mb: 2 }}>
          <MapContainer
            center={center}
            zoom={12}
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {locations.some(loc => loc.stop_lat && loc.stop_lon) && (
              <>
                {locations.map((location, index) => (
                  location.stop_lat && location.stop_lon && (
                    <Marker
                      key={index}
                      position={[parseFloat(location.stop_lat), parseFloat(location.stop_lon)]}
                    />
                  )
                ))}
              </>
            )}
            {optimizedRoute && (
              <>
                <Polyline
                  positions={optimizedRoute.map(loc => [loc.stop_lat, loc.stop_lon])}
                  color="blue"
                />
                {isSimulating && (
                  <BusSimulation
                    route={optimizedRoute}
                    isSimulating={isSimulating}
                    progress={progress}
                    onPositionUpdate={setBusInfo}
                  />
                )}
              </>
            )}
          </MapContainer>
        </Paper>

        {/* Real-time Dashboard */}
        {optimizedRoute && (
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Real-time Bus Information
            </Typography>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Current Location
                  </Typography>
                  <Typography variant="h6">
                    {optimizedRoute[busInfo.currentStop]?.stop_name || 'Starting Point'}
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Next Stop
                  </Typography>
                  <Typography variant="h6">
                    {optimizedRoute[busInfo.nextStop]?.stop_name || 'Final Destination'}
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={12} md={4}>
                <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Current Speed
                  </Typography>
                  <Typography variant="h6">
                    {busInfo.speed} km/h
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Distance to Next Stop
                  </Typography>
                  <Typography variant="h6">
                    {busInfo.remainingDistance} km
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={12} md={6}>
                <Paper sx={{ p: 2, bgcolor: 'background.default' }}>
                  <Typography variant="subtitle2" color="text.secondary">
                    Route Progress
                  </Typography>
                  <Typography variant="h6">
                    {busInfo.progress}%
                  </Typography>
                </Paper>
              </Grid>
            </Grid>
          </Paper>
        )}
      </Box>
    </Container>
  );
}

export default App;
